import React, { useState } from 'react'
import Im32 from '../Images/boy.png';
import { Link } from 'react-router-dom';

const MyProfile = (props) => {
    const [name, setName] = useState('');
    const [mobile, setMobile] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const handleReset = () => {
        setName('');
        setMobile('');
        setEmail('');
        setPassword('');
        setSubmitted(false);
      };
  return (
    <div>
             <div className="profile">
            <img src={Im32} alt="Cart" />
            <h1>My Account</h1>
            <p>Name: {props.name}</p>
            <p>Mobile: {props.mobile}</p>
            <p>Email: {props.email}</p>
           <Link to={'/'}> <button type="button" onClick={handleReset}>Countinue Shopping</button></Link>
            <button type="button"  onClick={handleReset}>Edit Info</button>
          
          </div>
    </div>
  )
}

export default MyProfile
