import React, { useEffect, useState } from 'react';
import './ProductDetailes.css';
import { useParams } from 'react-router-dom';
import items from '../Data';
import Product from './Product';

const ProductDetailes = ({ }) => {
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    setCart([...cart, item]);
  };
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [relatedProduct, setRelatedProduct] = useState([]);

  useEffect(() => {
    const filterProduct = items.filter((item) => item.id === Number(id));
    setProduct(filterProduct[0]);
    const relatedProduct = items.filter((item) => item.category === filterProduct[0]?.category);
    setRelatedProduct(relatedProduct);
  }, [id]);

  return (
    <>
      <div className="main-card">
        <div className='card-desc'>
          <img id='card' src={product.imgSrc} alt={product.title} />
        </div>
        <div className='card-desc'>
          <h5>{product.title}</h5>
          <p>{product.description}</p>
          <p className="price">$ {product.price}</p>
          <p>
            <button>Buy Now</button>
            <button onClick={addToCart}>Add to Cart</button>
          </p>
        </div>
      </div>
      <hr className='main-card-hr' />
      <h2 className='main-card-h2'>Related Products</h2>
      <Product items={relatedProduct} addToCart={addToCart} />
    </>
  );
}

export default ProductDetailes;
