import React, { useEffect, useState } from 'react';
import './ProductDetailes.css';
import { useParams } from 'react-router-dom';
import items from '../Data';

const ProductDetailes = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    console.log("ID from params:", id);
    console.log("Items array:", items);
    const category = 'desired-category'; // Replace this with the actual category you want to filter by
    const filterProduct = items.find(item => (item.id === Number(id) || item.id === id) && item.category === category);
    if (filterProduct) {
      setProduct(filterProduct);
    } else {
      console.error(`Product with id ${id} and category ${category} not found`);
    }
  }, [id]);
  

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div className="card">
        <img id="card" src={product.imgSrc} alt={product.title} />
        <h5>{product.title}</h5>
        <p>{product.description}</p>
        <p className="price">$ {product.price}</p>
        <p>
          <button>Buy Now</button>
          <button>Add to Cart</button>
        </p>
      </div>
    </>
  );
};

export default ProductDetailes;
