import React, { useState } from 'react';
import './App.css';
import {BrowserRouter as Router,Routes,Route} from 'react-router-dom';
import Home from './Components/Home/Home';
import Navbar from './Components/Navbar/Navbar';
import Footer from './Components/Navbar/Footer';
import Cart from './Components/Cart/Cart';
import Loginpage from './Components/Login/Loginpage';
import ProductDetailes from './Components/Product/ProductDetailes';
import Subnav from './Components/Navbar/Subnav';
import Product from './Components/Product/Product';
import MyProfile from './Components/Login/MyProfile';
import Additems from './Components/Product/Additems';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  const removeFromCart = (id) => {
    setCart(cart.filter(item => item.id !== id));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  const handleLogin = () => {
    setLoggedIn(true);
  };

  return (
    <div>
      <Router>
        <Navbar loggedIn={loggedIn} handleLogin={handleLogin} submitted={submitted}/>
        <Routes>
          <Route path='/' element={<Home/>} />
          <Route element={<Subnav/>}>
            <Route path='/products' element={<Product addToCart={addToCart}/>} />
          </Route>
          <Route path='/about' element={<Footer/>} />

          <Route path='/cart' element={<Cart cart={cart} removeFromCart={removeFromCart} />} />
          <Route path='/login' element={<Loginpage handleLogin={handleLogin} handleSubmit={handleSubmit} submitted={submitted}/>} />
          <Route path='/myprofile' element={<MyProfile handleLogout={handleLogout}/>}/>
          <Route path='/product/:id' element={<ProductDetailes addToCart={addToCart} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
