import React from "react";

import { Link } from "react-router-dom";
import {  toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Product = ({ items, cart, setCart }) => {

    const addToCart = (id, price, title, description, imgSrc) => {
        const obj = {
            id, price, title, description, imgSrc
        }
        setCart([...cart, obj]);
        console.log("Cart element = ", cart)
        toast.success('1 More add in cart', {
            position: "top-right",
            autoClose: 1500,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "dark",
        });
    }

    const addToBuy = () => {
        toast.success("order successfully");
    };
    return (
        <>
           
            <div classNameName="container my-5">
                <div classNameName="row">
                    {items.map((product) => {
                        return (
                            <>
                                <div key={product.id} classNameName="col-lg-3 col-md-6 my-3 text-center">

                                    <div class="card">
                                        <Link to={`/product/${product.id}`}
                                            style={{
                                                display: 'flex',
                                                justifyContent: 'center',
                                                alignItems: 'center',

                                            }}>

                                            <img
                                                src={product.imgSrc}
                                                alt="Product "
                                            />
                                        </Link>
                                        <h5>{product.title}</h5>
                                        <p className="price">â‚¹ {product.price}</p>
                                        <p>{product.description}</p>
                                        <p><button onClick={addToBuy}>Buy Now</button>
                                            <button onClick={() => addToCart(product.id, product.price, product.title, product.description, product.imgSrc)}>Add to Cart</button>
                                        </p>

                                    </div>
                                </div>
                            </>
                        );
                    })}
                </div>
            </div>
        </>
    );
};

export default Product;
