import React, { useState } from 'react'
import './Loginpage.css'
import Im31 from '../Images/shopping-bag.png';


const Loginpage = () => {
  const [name, setName] = useState('');
  const [mobile, setMobile] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    // Handle form submission
    console.log('Form submitted:', { name, mobile, email, password });
  };

  const handleReset = () => {
    setName('');
    setMobile('');
    setEmail('');
    setPassword('');
    setSubmitted(false);
  };
  return (
    <div>
      <div className="main-log-card">
        <img src={Im31} alt="Cart" />
        <h1>Welcome to M-cart</h1>
        {submitted ? (
          <div className="profile">
            <h2>Profile Information</h2>
            <p>Name: {name}</p>
            <p>Mobile: {mobile}</p>
            <p>Email: {email}</p>
            <button type="button" className='btn-reset' onClick={handleReset}>Edit Info</button>
          </div>
        ) : (
          <>
            <h2>Please enter your details to log in</h2>
            <form onSubmit={handleSubmit}>
              <input
                className='input-f'
                type="text"
                placeholder='Enter your name'
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <input
                className='input-f'
                type="tel"
                placeholder='Enter your mobile no.'
                value={mobile}
                onChange={(e) => setMobile(e.target.value)}
                required
              />
              <input
                className='input-f'
                type="email"
                placeholder='Enter your e-mail'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                className='input-f'
                type="password"
                placeholder='Enter your password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button type="submit" className='btn-submit'>Log In</button>
              <button type="button" className='btn-reset' onClick={handleReset}>Reset</button>
            </form>
          </>
        )}
      </div>
    </div>

  )
}

export default Loginpage
