import React from 'react';
import './Additems.css';
import { Link } from 'react-router-dom';

const Additems = (props) => {
  const handleAddToCart = () => {
    // Passing the item details to the parent (App.js) addToCart function
    props.addToCart({
      id: props.id,
      title: props.title,
      price: props.price,
      imgSrc: props.imgSrc
    });
  };

  return (
    <>
      <div className="card">
        <Link to={`/product/${props.id}`} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
          <img id="card" src={props.imgSrc} alt="" />
        </Link>
        <h5>{props.title}</h5>
        <p>{props.description}</p>
        <p className="price">$ {props.price}</p>
        <p>
          <button>Buy Now</button>
          <button onClick={handleAddToCart}>Add to Cart</button>
        </p>
      </div>
    </>
  );
};

export default Additems;
