import React, { useState } from 'react';
import './Subnav.css';
import items from '../Data';

const Subnav = () => {
    const [data,setData] = useState([...items])
    const [menu, setMenu] = useState("all-items"); 
    const filterBycategory =(category)=>{
        const element = items.filter((product)=>product.category === category)
        setData(element);
    }

    return (
        <div>
            <div className="nav-bar-wrapper">
                <div className="items">
                    <p onClick={() => { setMenu("all-items") }}>
                        All Products {menu === "all-items" && <hr />}
                    </p>
                </div>
                <div className="items" onClick={()=>filterBycategory('mobiles')}>
                    <p onClick={() => { setMenu("mobiles") }}>
                        Mobiles {menu === "mobiles" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("laptops") }}>
                        Laptops {menu === "laptops" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("earburds") }}>
                        Earbuds {menu === "earburds" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("others") }}>
                        Others {menu === "others" && <hr />}
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Subnav;
