// Additems.js
import React, { useState } from 'react';

const Additems = ({ addItemToCart }) => {
  const [item, setItem] = useState({ name: '', price: 0 });

  const handleAddClick = () => {
    if (item.name && item.price) {
      addItemToCart(item);
      setItem({ name: '', price: 0 }); // Reset the item input fields
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Item Name"
        value={item.name}
        onChange={(e) => setItem({ ...item, name: e.target.value })}
      />
      <input
        type="number"
        placeholder="Item Price"
        value={item.price}
        onChange={(e) => setItem({ ...item, price: parseFloat(e.target.value) })}
      />
      <button onClick={handleAddClick}>Add to Cart</button>
    </div>
  );
};

export default Additems;
