import React, { useEffect, useState } from 'react';
import './Subnav.css';
import { useParams } from 'react-router-dom';
import items from '../Data';

const Subnav = () => {
    const [menu, setMenu] = useState("all-items"); 
  const[product,setProduct]= useState({})
  
  useEffect(()=>{
    const filterProduct = items.filter((item)=>item.category === product.category);
    setProduct(filterProduct);
  },[product.category])

    return (
        <div>
            <div className="nav-bar-wrapper">
                <div className="items">
                    <p onClick={() => { setMenu("all-items") }}>
                        All Products {menu === "all-items" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("mobiles") }}>
                        Mobiles {menu === "mobiles" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("laptops") }}>
                        Laptops {menu === "laptops" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("earburds") }}>
                        Earbuds {menu === "earburds" && <hr />}
                    </p>
                </div>
                <div className="items">
                    <p onClick={() => { setMenu("others") }}>
                        Others {menu === "others" && <hr />}
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Subnav;
