import React, { useState } from 'react';
import './Additems.css';
import { Link } from 'react-router-dom';
import Modal from '../Product/Modal';

const Additems = ({ id, imgSrc, title, description, price, addToCart }) => {
  const [showModal, setShowModal] = useState(false);

  const handleBuyNow = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <>
      <div className="card">
        <Link to={`/product/${id}`} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', }}>
          <img id='card' src={imgSrc} alt={title} />
        </Link>
        <h5>{title}</h5>
        <p>{description}</p>
        <p className="price">$ {price}</p>
        <p>
          <button onClick={handleBuyNow}>Buy Now</button>
          <button onClick={() => addToCart()}>Add to Cart</button>
        </p>
      </div>
      <Modal show={showModal} handleClose={handleCloseModal} message="Thank you for your purchase!" />
    </>
  );
}

export default Additems;
